import {React,useState} from 'react'
// when the value of use state changes react rerenders its componenets..
function Home() {
  const [value,setvalue]=useState(0);
  const increment=()=>{
       setvalue(value+1);
  }
  const decrement=()=>{
        setvalue(value-1);
  }
  const btnstyle={
    padding:20,
    border:"none",
    backgroundColor:"#f0f0f0",
    margin:10,
    cursor:"pointer",
  }
  return (
    <div>
      <h1 style={{textAlign:"center",color:"black"}}>Hello</h1>
      <input style={{backgroundColor:"wheat",padding:20,margin:10,}}  placeholder='Enter value' value={value} />
      <button style={btnstyle} onClick={increment}>+</button>
      <button style={btnstyle} onClick={decrement}>-</button>
    </div>
  )
}

export default Home
